(function executeRule(current, previous /*null when async*/) {
	// API endpoint 
	gs.addInfoMessage("BusinessRule Triggered");
    var apiUrl = 'https://www.alphavantage.co/query?function=BALANCE_SHEET';
    var apiKey = '38a756289daffb0e571c309c71abfccd';
	gs.addInfoMessage("API URls defined");
    // Create REST message  
    var restMessage = new sn_ws.RESTMessageV2();
    // Set headers
    restMessage.setRequestHeader('Content-Type', 'application/json');
    // Loop through result set
    while (current) {
        // Get stock name
        var ticker = current.ticker;
		gs.addInfoMessage(ticker);
        // Call API  
        // Set endpoint and parameters
        restMessage.setEndpoint(apiUrl + "&symbol=" +ticker + "&apikey=demo");
        restMessage.setHttpMethod('GET');
        // Execute request
        var response = restMessage.execute();
        var responseBody = response.getBody();
        gs.log(responseBody);
        // Parse and print price
        var stockData = JSON.parse(responseBody);
		gs.info(stockData.annualReports[0].commonStockSharesOutstanding);
		var SharesOutstanding = stockData.annualReports[0].commonStockSharesOutstanding;
        gs.info(ticker + ' SharesOutstanding: ' + SharesOutstanding);
        // Update price field
        current.u_outstanding_shares = SharesOutstanding;
        // Update record
        current.update();
    }

	// Add your code here

})(current, previous);
